def findPattern(text, pattern):
    i=0
    count=0
    while i < len(text):
        j = 0
        if text[i] == pattern[j]:
            k = i
            while j < len(pattern) and k < len(text) and  text[k] == pattern[j]:
                j+=1
                k+=1
            if j == len(pattern):
                count+=1
            i+=1
        else:
            i+=1
    return count

