def matrix_multi(A, B):
    rows_A = len(A)
    cols_A = len(A[0])
    rows_B = len(B)
    cols_B = len(B[0])

    if cols_A != rows_B:
        print("Cannot multiply the given matrices.")
        return

    C = [[0 for row in range(cols_B)] for col in range(rows_A)]

