def solve(list):
    max_len = 0
    longest_prefix = [0] * len(list)
    for i in range(1, len(list)):
        if list[i] == list[i-1]:
            longest_prefix[i] = longest_prefix[i-1] + 1
        else:
            longest_prefix[i] = 0
        max_len = max(max_len, longest_prefix[i])
