    keys = data[0].keys()

    # Check column type
    sidx = [ idx for idx, key in enumerate(keys) if is_string(data[0][key]) ]

    values = [ [ d.get(key) for key in keys ] for d in data ]
    values = zip(*values)

    if len(sidx) == 1:
        text = values.pop(sidx[0])
        keys.pop(sidx[0])
    else:
        text = None

    return {
        'layout': {
            'axes': keys
        },
        'data': {
            'values': values,
            'text': text
        }
    }


