def binarySearch2D(arr, key):
    start = 0
    end = len(arr) - 1
    while start <= end:
        mid = (start + end) // 2
        if arr[mid][0] == key:
            return True
        elif arr[mid][0] > key:
            end = mid - 1
        else:
            start = mid + 1
    return False

