                dp[i][j] = dp[i - 1][j - 1] + 1
            else:
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])

    def backtrack(i, j, subsequence):
        if i == 0 or j == 0:
            return [subsequence[::-1]]

        if main_string[i - 1] == reference_string[j - 1]:
            return backtrack(i - 1, j - 1, main_string[i - 1] + subsequence)
        elif dp[i - 1][j] >= dp[i][j - 1]:
            return backtrack(i - 1, j, subsequence)
        else:
            return backtrack(i, j - 1, subsequence)

